package com.kaurihealth.chatlib.cache;

import java.io.Serializable;

/**
 * Created by wli on 16/2/2.
 * LCChatKit 中的用户类，仅包含三个变量，暂不支持继承扩展
 */
public final class LCChatKitUser implements Serializable {
    private String clientId;
    private String avatar;
    private String fullName;

    public LCChatKitUser() {
    }

    public LCChatKitUser(String clientId, String fullName, String avatar) {
        this.clientId = clientId;
        this.avatar = avatar;
        this.fullName = fullName;
    }

    public String getClientId() {
        return clientId;
    }

    public String getAvatar() {
        return avatar;
    }

    public String getFullName() {
        return fullName;
    }


}
