public class RelationMessageBean {
    private String conversationId;
    private ContactUserDisplayBean bean;

    public RelationMessageBean(String conversationId, ContactUserDisplayBean bean) {
        this.conversationId = conversationId;
        this.bean = bean;
    }

    public String getConversationId() {
        return conversationId;
    }

    public void setConversationId(String conversationId) {
        this.conversationId = conversationId;
    }

    public ContactUserDisplayBean getBean() {
        return bean;
    }

    public void setBean(ContactUserDisplayBean bean) {
        this.bean = bean;
    }
}
